import { connect } from "react-redux";
import Table from "./Table";
const filterdatabyfilbykeyandvalue = (data, filterby, FilterValue) => {
  console.log(filterby,'filterbyfilterbyfilterbyfilterbyfilterbyfilterbyfilterby',FilterValue,'FilterValue')
  if(!filterby==='All'){

    return data.filter((data) => data.name === FilterValue.toLowerCase().trim());

  }else{
    return data
  }

};
const getFilterCities = (data, filterby, FilterValue) => {
  console.log(
    filterdatabyfilbykeyandvalue(data, filterby, FilterValue),
    "filterdatabyfilbykeyandvalue"
  );
  let cities = [];
  if (filterby === "All" || FilterValue === "All") {
    cities = data.map((obj) => obj);
  } else {
    cities = data
      .filter((obj) => obj[`${filterby}`] === FilterValue)
      .map((obj) => {
        return obj;
      });
    console.log(cities, "+++++++++++data++++++++++++++");
  }
  return cities;
};

const mapStateToProps = (state) => ({
  cities: getFilterCities(
    state.data,
    state.visibilityFilter.filterbyKey,
    state.visibilityFilter.FilterValue
  ),
  filterby: state.visibilityFilter.filterbyKey,
  FilterValue: state.visibilityFilter.FilterValue,
});

export default connect(mapStateToProps)(Table);
