import { connect } from "react-redux";
import Table from "./Table";

const getFilterCities = (data, filterby, FilterValue) => {
  const filterdatabyfilbykeyandvalue = () => {
    switch (filterby) {
      case "cluster":
        return data.filter(
          (data) =>
            data.cluster ===
            data.toLowerCase().includes(FilterValue.toLowerCase())
        );
      case "city":
        return data.filter(
          (data) =>
            data.city === data.toLowerCase().includes(FilterValue.toLowerCase())
        );
      case "space_available":
        return data.filter(
          (data) =>
            data.space_available ===
            data.toLowerCase().includes(FilterValue.toLowerCase())
        );
      default:
        return data;
    }
  };
  console.log(filterdatabyfilbykeyandvalue, "filterdatabyfilbykeyandvalue");
  let cities = [];
  if (filterby === "All" || FilterValue === "All") {
    cities = data.map((obj) => obj);
  } else {
    cities = data
      .filter((obj) => obj[`${filterby}`] === FilterValue)
      .map((obj) => {
        return obj;
      });
    console.log(cities, "+++++++++++data++++++++++++++");
  }
  return cities;
};

const mapStateToProps = (state) => ({
  cities: getFilterCities(
    state.data,
    state.visibilityFilter.filterbyKey,
    state.visibilityFilter.FilterValue
  ),
  filterby: state.visibilityFilter.filterbyKey,
  FilterValue: state.visibilityFilter.FilterValue,
});

export default connect(mapStateToProps)(Table);
