import React, { useState,useEffect } from "react";
import { useDispatch, useSelector } from "react-redux";

import PropTypes from "prop-types";
import { updateWareHouse } from "../actions/filter";
import { useHistory } from "react-router-dom";

function editpage(props) {
  const location = useHistory();
  const dispatch = useDispatch();
  const wrhouse = useSelector((state) => state.data[Number(getidfromurl())]);

  const [inputField, setInputField] = useState({});
  const inputsHandler = (e) => {
    setInputField({ ...inputField, [e.target.name]: e.target.value });
  };
  const validateinputFieldprops = () => {
    let isValid = true;
    for (let key in inputField) {
      if (inputField[key] === "") {
        isValid = false;
      }
    }
    return isValid;
  };
  function getidfromurl() {
    var url = window.location.href;
    var id = url.substring(url.lastIndexOf("/") + 1);
    return id;
  }

  const submitButton = () => {
    if (validateinputFieldprops()) {
      dispatch(
        updateWareHouse({ value: inputField, id: Number(getidfromurl()) })
      );
      location.push("/");

      return alert("updatedI");
    } else {
      return alert("Please fill all the fields");
    }
  };

  useEffect(() => {
    setInputField(wrhouse);
  }, []);
  return (
    <div>
      <div className="absolute top-0 bottom-0 left-0 right-0 flex flex-col w-full h-full bg-white">
        <div className="w-full max-w-md p-4 mx-auto my-20 border shadow-sm">
          <div className="flex items-center justify-end">
            <button
              onClick={(e) => {
                toggleisAdding();
              }}
            >
              <div className="flex items-center justify-end">
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  className="w-6 h-6"
                  fill="none"
                  viewBox="0 0 24 24"
                  stroke="currentColor"
                >
                  <path
                    strokeLinecap="round"
                    strokeLinejoin="round"
                    strokeWidth="2"
                    d="M6 18L18 6M6 6l12 12"
                  />
                </svg>
                close
              </div>
            </button>
          </div>
          <br />
          <input
            type="text"
            name="name"
            className="flex w-full "
            onChange={(e) => {
              inputsHandler(e);
            }}
            placeholder="enter Warehouse name"
            value={inputField.name}
          />
          <br />
          <input
            className="flex w-full "
            type="text"
            name="code"
            onChange={(e) => {
              inputsHandler(e);
            }}
            placeholder="enter Warehouse code"
            value={inputField.code}
          />
          <br />
          <input
            className="flex w-full "
            type="text"
            name="city"
            onChange={(e) => {
              inputsHandler(e);
            }}
            placeholder="enter Warehouse city"
            value={inputField.city}
          />{" "}
          <br />
          <input
            className="flex w-full "
            type="Number"
            name="space_available"
            onChange={(e) => {
              inputsHandler(e);
            }}
            placeholder="enter Warehouse space_available"
            value={inputField.space_available}
          />
          <br />
          <input
            className="flex w-full "
            type="text"
            name="type"
            onChange={(e) => {
              inputsHandler(e);
            }}
            placeholder="enter Warehouse type"
            value={inputField.type}
          />
          <br />{" "}
          <input
            className="flex w-full "
            type="text"
            name="cluster"
            onChange={(e) => {
              inputsHandler(e);
            }}
            placeholder="enter Warehouse cluster"
            value={inputField.cluster}
          />
          <br />
          <label>
            is_registered : {inputField.is_registered ? "yes" : "no"}
          </label>
          <input
            className="flex w-full "
            className="ml-2"
            name="is_registered"
            onChange={(e) => {
              setInputField({...inputField, is_registered: e.target.value})
            }}
            checked={inputField.is_registered}
            value={Boolean(inputField.is_registered)}
            type="checkbox"
          />{" "}
          <br />
          <label>is_live : {inputField.is_live ? "yes" : "no"} </label>
          <input
            className="ml-2"
            name="is_live"
            checked={inputField.is_live}

            value={Boolean(inputField.is_live)}
            type="checkbox"
          />{" "}
          <br />
          <div className="flex justify-center">
            <button className="r active " onClick={submitButton}>
              Submit Now
            </button>
          </div>
        </div>
      </div>
    </div>
  );
}

editpage.propTypes = {};

export default editpage;
