import React, { useState } from "react";
import "./Table.css";
import Search from "./../Search";
import { useDispatch, useSelector } from "react-redux";
import { Link } from "react-router-dom";

import { setSearchTerm } from "./../../actions/filter";
/*

 */

const Table = ({ cities, filterby, FilterValue }) => {
  const dispatch = useDispatch();
  const [isAdding, SetisAdding] = useState(false);
  const [inputField, setInputField] = useState({
    first_name: "",
    last_name: "",
    gmail: "",
  });

  const inputsHandler = (e) => {
    setInputField({ [e.target.name]: e.target.value });
  };

  const submitButton = () => {
    alert(inputField.first_name);
  };

  const toggleisAdding = () => {
    SetisAdding(!isAdding);
  };

  const srcsltct = useSelector((state) => state.visibilityFilter.searchValue);
  const [searchTerm, SetsearchTerm] = useState("");
  const data = cities.filter((data, index) => {
    if (searchTerm) {
      console.log(searchTerm, "^^^^^^^^");
    }
    return data;
  });
  const renderTable = data.map((city, i) => {
    return (
      <>
        <tr key={i}>
          <td>{i + 1}</td>
          <td>{city.name}</td>
          <td>{city.code}</td>
          <td>{city.id}</td>
          <td>{city.city}</td>
          <td>{city.space_available}</td>
          <td>{city.type}</td>
          <td>{city.cluster}</td>
          <td>{city.is_registered ? "yes" : "no"}</td>
          <td>{city.is_live ? "yes" : "no"}</td>
          <td>
            <Link
              className="flex w-full text-blue-400"
              to={`/warehouse/${city.id}`}
            >
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className="w-6 h-6"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M11 5H6a2 2 0 00-2 2v11a2 2 0 002 2h11a2 2 0 002-2v-5m-1.414-9.414a2 2 0 112.828 2.828L11.828 15H9v-2.828l8.586-8.586z"
                />
              </svg>{" "}
              Edit
            </Link>
          </td>
        </tr>
      </>
    );
  });
  return (
    <>
      Search term: {srcsltct} <br />
      Filter:{FilterValue} <br />
      Filterby: {filterby}
      <div className="flex items-center justify-between">
        <Search
          value={searchTerm}
          onChange={(e) => {
            dispatch(setSearchTerm({ value: e.target.value }));
            console.log(e.target.value);
            SetsearchTerm(e.target.value);
          }}
        />

        <button
          onClick={(e) => {
            toggleisAdding();
          }}
        >
          {isAdding ? (
            <div className="flex items-center">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className="w-6 h-6"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M6 18L18 6M6 6l12 12"
                />
              </svg>
              close
            </div>
          ) : (
            <div className="flex items-center">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                className="w-6 h-6"
                fill="none"
                viewBox="0 0 24 24"
                stroke="currentColor"
              >
                <path
                  strokeLinecap="round"
                  strokeLinejoin="round"
                  strokeWidth="2"
                  d="M12 6v6m0 0v6m0-6h6m-6 0H6"
                />
              </svg>{" "}
              Add
            </div>
          )}
        </button>
      </div>{" "}
      {isAdding ? (
        <>
          <div>
            <input
              type="text"
              name="first_name"
              onChange={inputsHandler}
              placeholder="First Name"
              value={inputField.first_name}
            />
            <br />
            <input
              type="text"
              name="last_name"
              onChange={inputsHandler}
              placeholder="First Name"
              value={inputField.last_name}
            />
            <br />
            <input
              type="gmail"
              name="gmail"
              onChange={inputsHandler}
              placeholder="Gmail"
              value={inputField.gmail}
            />
            <br />
            <button onClick={submitButton}>Submit Now</button>
          </div>
        </>
      ) : (
        <>
          {" "}
          <table>
            <thead>
              <tr>
                <th>S.no</th>
                <th>name</th>
                <th>code</th>
                <th>id</th>
                <th>city</th>
                <th>space_available</th>
                <th>type</th>
                <th>cluster</th>
                <th>is_registered</th>
                <th>is_live</th> <th>Action</th>
              </tr>
            </thead>
            <tbody>
              {data.length > 0 ? (
                <>{renderTable}</>
              ) : (
                <>
                  <div className="container absolute w-full">
                    <div className="flex justify-center my-20 text-center">
                      No Data Found
                    </div>
                  </div>
                </>
              )}
            </tbody>
          </table>
        </>
      )}
    </>
  );
};

export default Table;
